import React, { useState } from 'react'
import CustomModal from './Modal'

export default function Card({ item, vol, add }) {
	const [count, setCount] = useState(0)
	const [isVisible, setIsVisible] = useState(true)
	const [isModalOpen, setIsModalOpen] = useState(false)

	const handleAddClick = () => {
		setIsModalOpen(true)
		// setIsVisible(false)
		// setCount(1)
	}

	const closeModal = () => {
		setIsModalOpen(false)
	}

	const increment = () => {
		setCount(count + 1)
	}

	const decrement = () => {
		if (count > 1) {
			setCount(count - 1)
		} else {
			setIsVisible(true)
			setCount(0)
		}
	}

	const productVolumes = vol.filter(volume => volume.id_good === item.id)

	return (
		<div className='card'>
			<div className='card--title'>
				<p>{item.name}</p>
			</div>
			<div className='card--img'>
				<img src={'http://' + item.photo} alt={item.name} />
			</div>
			<div className='card--position'>
				{productVolumes.map(volume => (
					<div key={volume.id} className='card--priceVol'>
						<div className='card--price'>
							<span>{volume.price} руб.</span>
						</div>
						{volume.volume ? (
							<div className='card--volume'>
								<span>{volume.volume} мл</span>
							</div>
						) : (
							<></>
						)}
					</div>
				))}
				<div className='card--counter'>
					<button onClick={handleAddClick} className='counter--addBtn'>
						Добавить
					</button>
					{/* {isVisible ? (
					<button onClick={handleAddClick} className='counter--addBtn'>
						Добавить
					</button>
				) : (
					<div className='counter--interface'>
						<div>
							<button onClick={decrement} className='counter--decrement'>
								-
							</button>
						</div>
						<div>
							<span>{count}</span>
						</div>
						<div>
							<button onClick={increment} className='counter--increment'>
								+
							</button>
						</div>
					</div>
				)} */}
				</div>
			</div>
			<CustomModal
				isOpen={isModalOpen}
				onRequestClose={closeModal}
				item={item}
				vol={vol}
				add={add}
			/>
		</div>
	)
}
