import React, { useState } from 'react'

export default function Card({ item }) {
	const [count, setCount] = useState(0)
	const [isVisible, setIsVisible] = useState(true)

	const handleAddClick = () => {
		setIsVisible(false)
		setCount(1)
	}

	const increment = () => {
		setCount(count + 1)
	}

	const decrement = () => {
		if (count > 1) {
			setCount(count - 1)
		} else {
			setIsVisible(true)
			setCount(0)
		}
	}

	return (
		<div className='card'>
			<div className='card--title'>
				<p>{item.name}</p>
			</div>
			<div className='card--img'>
				<img src={item.photo} alt='Item_photo' />
			</div>
			<div className='card--counter'>
				{isVisible ? (
					<button onClick={handleAddClick} className='counter--add'>
						Добавить
					</button>
				) : (
					<>
						<button onClick={decrement}>-</button>
						<span>{count}</span>
						<button onClick={increment}>+</button>
					</>
				)}
			</div>
		</div>
	)
}
