import React from 'react'
import Modal from 'react-modal'

Modal.setAppElement('#root') // Установите элемент, который будет использоваться для фокуса

const CustomModal = ({ isOpen, onRequestClose, item, vol }) => {
	const productVolume = vol.find(vol => vol.id_good === item.id)

	return (
		<Modal
			isOpen={isOpen}
			onRequestClose={onRequestClose}
			contentLabel='Product Details'
			className='modal'
			overlayClassName='overlay'
		>
			<div className='modal-content'>
				<h2>{item.name}</h2>
				<img
					src={item.photo || '/images/default.png'}
					alt={item.name}
					className='modal-image'
				/>
				<p>{item.general_info}</p>
				{productVolume && (
					<div className='modal-details'>
						<p>{productVolume.volume} мл</p>
						<p>{productVolume.price} руб.</p>
					</div>
				)}
				<button onClick={onRequestClose} className='modal-close-btn'>
					Закрыть
				</button>
			</div>
		</Modal>
	)
}

export default CustomModal
