import './App.css'
import React, { useState, useEffect } from 'react'

function App() {
	const [goods, setGoods] = useState([])

	async function getItems() {
		const url = 'http://localhost:3000/api/goods'
		try {
			const response = await fetch(url)
			const result = await response.json()
			console.log(result)
			setGoods(result.Goods)
		} catch (error) {
			console.error(error)
		}
	}
	useEffect(() => {
		getItems()
	}, [])
	useEffect(() => {
		console.log(goods)
	}, [goods])

	const goodsDivs = goods.map((item, index) => (
		<div key={index} className='card'>
			<div className='card--title'>
				<p>{item.name}</p>
			</div>
			<div className='card--img'>
				<img src={item.photo} alt='Item_photo' />
			</div>
		</div>
	))

	return (
		<div className='container'>
			<header>
				<img src='/images/header.png' alt='header--img' />
			</header>
			<main>
				<div className='grid'>{goodsDivs}</div>
			</main>
		</div>
	)
}

export default App
