import React, { useState } from 'react'

export default function Card({ item, vol }) {
	const [count, setCount] = useState(0)
	const [isVisible, setIsVisible] = useState(true)

	const handleAddClick = () => {
		setIsVisible(false)
		setCount(1)
	}

	const increment = () => {
		setCount(count + 1)
	}

	const decrement = () => {
		if (count > 1) {
			setCount(count - 1)
		} else {
			setIsVisible(true)
			setCount(0)
		}
	}

	const productVolumes = vol.find(volume => volume.id_good === item.id)

	return (
		<div className='card'>
			<div className='card--title'>
				<p>{item.name}</p>
			</div>
			<div className='card--img'>
				<img src={item.photo} alt='Item_photo' />
			</div>
			<div className='card--priceVol'>
				{productVolumes.map(volume => (
					<div key={volume.id}>
						<span>{volume.price}</span>
						<span>{volume.volume}</span>
					</div>
				))}
			</div>
			<div className='card--counter'>
				{isVisible ? (
					<button onClick={handleAddClick} className='counter--addBtn'>
						Добавить
					</button>
				) : (
					<div className='counter--interface'>
						<div>
							<button onClick={decrement} className='counter--decrement'>
								-
							</button>
						</div>
						<div>
							<span>{count}</span>
						</div>
						<div>
							<button onClick={increment} className='counter--increment'>
								+
							</button>
						</div>
					</div>
				)}
			</div>
		</div>
	)
}
